export default {
  name: 'Register',

  data() {
    return {
      phone: '',
      code: '',
      password: '',
      password1: '',
      agree: true
    };
  },

  methods: {
    async getCode() {
      try {
        const {
          phone
        } = this;
        phone && (await this.$store.dispatch('getCode', phone));
        this.code = this.$store.state.user.code;
      } catch (error) {
        error.message();
      }
    },

    async userRegister() {
      const success = await this.$validator.validateAll(); //如果全部验证成功再发请求

      if (success) {
        //用户注册
        try {
          const {
            phone,
            code,
            password,
            password1
          } = this;
          await this.$store.dispatch('userRegister', {
            phone,
            code,
            password
          });
          this.$router.push('/login');
        } catch (error) {
          alert(error.message);
        }
      }
    }

  }
};
