import Swiper from "swiper";
export default {
  name: "ImageList",
  data: function data() {
    return {
      currentIndex: 0
    };
  },
  props: ['skuImageList'],
  methods: {
    changeCurrentIndex: function changeCurrentIndex(index) {
      this.currentIndex = index; //通知兄弟组件，当前的索引值为几

      this.$bus.$emit('getIndex', this.currentIndex);
    }
  },
  watch: {
    skuImageList: function skuImageList(newV, oldV) {
      this.$nextTick(function () {
        new Swiper('.swiper-container', {
          loop: true,
          // 循环模式选项
          slidesPerView: 3,
          // 如果需要前进后退按钮
          navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev'
          }
        });
      });
    }
  }
};
