export default {
  name: "Pagination",
  props: ['pageNo', 'pageSize', 'total', 'continues'],
  computed: {
    //总共多少页
    totalPage: function totalPage() {
      //向上取整
      return Math.ceil(this.total / this.pageSize);
    },
    //计算出连续的页码的起始数字与结束数字【连续页码的数字至少是5 】
    startNumAndEndNum: function startNumAndEndNum() {
      var pageNo = this.pageNo,
          continues = this.continues,
          totalPage = this.totalPage; //先定义两个变量存储起始数字与结束数字

      var start = 0,
          end = 0; //连续页码数字5【就是至少5页】，如果出现不正常现象【就是不够5页】
      //不正常现象【总页数没有了连续页码多】

      if (continues > totalPage) {
        start = 1;
        end = totalPage;
      } else {
        //正常现象【连续页码数字是5，总页数一定是大于5】
        start = pageNo - parseInt(continues / 2);
        end = pageNo + parseInt(continues / 2); //把出现不正常现象【start数字出现0】纠正

        if (start < 1) {
          start = 1; //end是连续的页码数

          end = continues;
        } //把出现不正常现象【end数字总数大于总页码】纠正


        if (end > totalPage) {
          end = totalPage;
          start = totalPage - continues + 1;
        }
      }

      return {
        start: start,
        end: end
      };
    }
  }
};
