export default {
  name: 'Pay',

  data() {
    return {
      payInfo: {}
    };
  },

  mounted() {
    this.getPayInfo();
  },

  methods: {
    async getPayInfo() {
      let result = await this.$API.reqPayInfo();
      console.log(result);

      if (result.data.code == 200) {
        this.payInfo = result.data.data.data;
      }
    },

    open() {
      let url = "https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fup.enterdesk.com%2Fedpic%2F54%2F45%2Ffe%2F5445fee00862d5d3bc93488d42da0c22.jpg&refer=http%3A%2F%2Fup.enterdesk.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=jpeg?sec=1648961272&t=3dfd90284b8613b4f34ff2421261a209";
      this.$alert(`<img src=${url} style="height: 200px" />`, '扫码支付', {
        dangerouslyUseHTMLString: true,
        center: true,
        showCancelButton: true,
        cancelButtonText: '支付遇见问题',
        confirmButtonText: '支付成功',
        showClose: true
      });
    }

  }
};
