import {reqCarList} from "../../api";


const state={
  CartList:[]
}

const mutations={
  GETCARTLIST(state,getCartList){
    state.CartList = getCartList
  }
}

const actions={
  //获取购物车列表数据
  async getCartList({commit}){
    let result = await reqCarList();
    //console.log(result.data);
    if (result.status==200){
      commit('GETCARTLIST',result.data)
    }
  }
}

const getters={
  cartList(state){
    return state.cartList[0]||{}
  }
}

export default {
  state,
  mutations,
  actions,
  getters
}
