//登录与注册的模块
import {reqGetCode} from "../../api";


const state = {};

const mutations = {};

const actions = {
  async getCode({commit},phone){
    let result = await reqGetCode(phone);
    console.log(result);
    if (result.data.code==200){
      commit('GETCODE',this.data)
    }
  }
};


const getters = {};

export default {
  state,
  mutations,
  actions,
  getters
}
