import ImageList from './ImageList';
import Zoom from './Zoom';
import { mapGetters } from "vuex";
export default {
  name: 'Detail',

  data() {
    return {
      //购买产品的个数
      skuNum: 1
    };
  },

  components: {
    ImageList,
    Zoom
  },

  mounted() {
    this.$store.dispatch('getGoodInfo', this.$route.params.skuid);
  },

  computed: { ...mapGetters(['categoryView', 'skuInfo', 'spuSaleAttrList']),

    skuImageList() {
      return this.skuInfo.skuImageList || [];
    }

  },
  methods: {
    //产品售卖属性切换高亮
    changeActive(spuSale, arr) {
      //console.log(spuSale)
      //遍历全部售卖属性值isChecked为零没有高亮了
      arr.forEach(item => {
        item.isChecked = 0;
      }); //点击的那个售卖属性值

      spuSale.isChecked = 1;
    },

    //表单元素修改产品个数
    changeSkuNum(e) {
      let value = e.target.value * 1;

      if (isNaN(value) || value < 1) {
        this.skuNum = 1;
      } else {
        this.skuNum = parseInt(value);
      }
    },

    async addShopcar() {
      try {
        let result = await this.$store.dispatch('AddOrUpdateShopCart', {
          skuId: this.$route.params.skuid,
          skuNum: this.skuNum
        });
        sessionStorage.setItem('SKUINFO', JSON.stringify(this.skuInfo));
        this.$router.push({
          name: 'addCartSuccess'
        });
      } catch (error) {
        alert(error.message);
      }
    }

  }
};
