import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

import Home from "../views/Home";
import Login from "../views/Login";
import Register from "../views/Register";
import Search from "../views/Search";

//重写push|replace 方法
let originPush = VueRouter.prototype.push;


const routes = [
  {
    path: '/',
    redirect: '/home'
  },
  {
    path:'/home',
    name:'home',
    component:Home,
    meta: {show:true}
  },
  {
    path:'/login',
    name:'login',
    component:Login,
    meta: {show: false}
  },
  {
    path:'/register',
    name:'register',
    component:Register,
    meta: {show:false}
  },
  {
    path:'/search/:keyword',
    name:'search',
    component:Search,
    meta: {show: true}
  },
]

const router = new VueRouter({
  routes
})

export default router
