import Home from "@/views/Home";
import Detail from "@/views/Detail";
import Login from "@/views/Login";
import Register from "@/views/Register";
import Search from "@/views/Search";
import AddCartSuccess from "../views/AddCartSuccess";

export default [
  {
    path: '/',
    redirect: '/home'
  },
  {
    path:'/addCartSuccess',
    name:'addCartSuccess',
    component:AddCartSuccess,
    meta: {show:true},
  },
  {
    path:'/home',
    name:'home',
    component:Home,
    meta: {show:true},
  },
  {
    path:'/Detail/:skuid',
    name:'detail',
    component:Detail,
    meta: {show:true},
  },
  {
    path:'/login',
    name:'login',
    component:Login,
    meta: {show: false}
  },
  {
    path:'/register',
    name:'register',
    component:Register,
    meta: {show:false}
  },
  {
    path:'/search/:keyword?',
    name:'search',
    component:Search,
    meta: {show: true}
  },
]
