import Home from "@/views/Home";
import Detail from "@/views/Detail";
import Login from "@/views/Login";
import Register from "@/views/Register";
import Search from "@/views/Search";
import AddCartSuccess from "../views/AddCartSuccess";
import ShopCart from "../views/ShopCart";
import Trade from "../views/Trade";
import Pay from "../views/Pay";
import Center from "../views/Center";
import myOrder from "../views/Center/myOrder";
import groupOrder from "../views/Center/groupOrder";

export default [
  {
    path: '/',
    redirect: '/home'
  },
  {
    path:'/center',
    name:'center',
    component:Center,
    meta: {show:true},
    children:[
      {
        path:'myOrder',
        component:myOrder
      }
    ]
  },
  {
    path:'/pay',
    name:'pay',
    component:Pay,
    meta: {show:true},
  },
  {
    path:'/trade',
    name:'trade',
    component:Trade,
    meta: {show:true},
  },
  {
    path:'/shopCart',
    name:'shopCart',
    component:ShopCart,
    meta: {show:true},
  },
  {
    path:'/addCartSuccess',
    name:'addCartSuccess',
    component:AddCartSuccess,
    meta: {show:true},
  },
  {
    path:'/home',
    name:'home',
    component:Home,
    meta: {show:true},
  },
  {
    path:'/Detail/:skuid',
    name:'detail',
    component:Detail,
    meta: {show:true},
  },
  {
    path:'/login',
    name:'login',
    component:Login,
    meta: {show: false}
  },
  {
    path:'/register',
    name:'register',
    component:Register,
    meta: {show:false}
  },
  {
    path:'/search/:keyword?',
    name:'search',
    component:Search,
    meta: {show: true}
  },
]
